import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { RatesService, Destino } from '../../services/rates.service';

@Component({
  selector: 'app-calculator',
  templateUrl: './calculator.component.html',
  styleUrls: ['./calculator.component.scss']
})
export class CalculatorComponent implements OnInit {
  calculatorForm: FormGroup;
  result: any = null;
  showResult = false;
  loading = false;
  destinos: Destino[] = [];
  error: string | null = null;

  constructor(
    private fb: FormBuilder,
    private ratesService: RatesService
  ) {
    this.calculatorForm = this.fb.group({
      destino_id: ['', Validators.required],
      peso: ['', [Validators.required, Validators.min(0.1), Validators.max(30)]]
    });
  }

  ngOnInit() {
    this.cargarDestinos();
  }

  cargarDestinos() {
    this.ratesService.obtenerDestinos().subscribe({
      next: (data) => {
        this.destinos = data;
      },
      error: (err) => {
        this.error = 'Error al cargar los destinos';
        console.error(err);
      }
    });
  }

  onSubmit() {
    if (this.calculatorForm.invalid) {
      return;
    }

    this.loading = true;
    this.error = null;

    this.ratesService.calcularEnvio(this.calculatorForm.value).subscribe({
      next: (response) => {
        this.result = response;
        this.showResult = true;
        this.loading = false;
      },
      error: (err) => {
        this.error = err.error?.error || 'Error al calcular el precio';
        this.loading = false;
      }
    });
  }

  onClear() {
    this.calculatorForm.reset();
    this.result = null;
    this.showResult = false;
    this.error = null;
  }
}
