  closeModal() {
    this.showModal = false;
    document.body.style.overflow = 'auto'; // Restaurar scroll
  }

  onSubmit() {
    if (this.calculatorForm.invalid) {
      return;
    }

    this.loading = true;
    this.error = null;

    const { destino, peso } = this.calculatorForm.value;

    let categoria = '';
    let precio = 0;

    if (peso < 0.1) {
      categoria = 'Sobre';
      precio = this.tarifas[destino].sobre;
    } else if (peso < 1) {
      categoria = 'Paquete menos de 1kg';
      precio = this.tarifas[destino].menos1kg;
    } else if (peso <= 10) {
      categoria = 'Cajas 1-10kg';
      precio = this.tarifas[destino].cajas1_10;
    } else if (peso <= 20) {
      categoria = 'Cajas 11-20kg';
      precio = this.tarifas[destino].cajas11_20;
    } else {
      categoria = 'Cajas 21-30kg';
      precio = this.tarifas[destino].cajas21_30;
    }

    setTimeout(() => {
      this.result = {
        destino: destino,
        peso: peso,
        categoria: categoria,
        precio: precio
      };
      this.showResult = true;
      this.loading = false;
    }, 500);
  }

  onClear() {
    this.calculatorForm.reset();
    this.result = null;
    this.showResult = false;
    this.error = null;
  }
