import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';

export interface Destino {
  id: number;
  nombre: string;
}

export interface CalculoResponse {
  precio: number;
  destino: string;
  peso: number;
  rango: string;
}

@Injectable({
  providedIn: 'root'
})
export class RatesService {
  private apiUrl = 'http://localhost:3000/api';

  constructor(private http: HttpClient) {}

  obtenerDestinos(): Observable<Destino[]> {
    return this.http.get<Destino[]>(`${this.apiUrl}/destinos`);
  }

  calcularEnvio(datos: { destino_id: number, peso: number }): Observable<CalculoResponse> {
    return this.http.post<CalculoResponse>(`${this.apiUrl}/calcular`, datos);
  }
}
